let cart = [];
const menuContainer = document.getElementById("menu-container");
const searchInput = document.getElementById("search");
const cartItemsDiv = document.getElementById("cart-items");
const totalSpan = document.getElementById("total");

function displayMenu(items) {
  menuContainer.innerHTML = "";

  items.forEach(item => {
    const card = document.createElement("div");
    card.classList.add("card");

    card.innerHTML = `
      <img src="${item.img}" alt="${item.name}">
      <div class="content">
        <h3>${item.name}</h3>
        <p>Category: ${item.category}</p>
        <p class="price">₹${item.price}</p>
        <button onclick="addToCart(${item.id})">Add to Cart</button>
      </div>
    `;

    menuContainer.appendChild(card);
  });
}

function addToCart(id) {
  const item = menu.find(food => food.id === id);
  cart.push(item);
  updateCart();
}

function updateCart() {
  cartItemsDiv.innerHTML = "";
  let total = 0;

  cart.forEach((item) => {
    total += item.price;

    cartItemsDiv.innerHTML += `
      <div class="cart-item">
        <span>${item.name}</span>
        <span>₹${item.price}</span>
      </div>
    `;
  });

  totalSpan.textContent = total;

  // Save cart in localStorage
  localStorage.setItem("cart", JSON.stringify(cart));
  localStorage.setItem("total", total);
}

document.querySelectorAll(".filter-btn").forEach(btn => {
  btn.addEventListener("click", () => {
    document.querySelectorAll(".filter-btn").forEach(b => b.classList.remove("active"));
    btn.classList.add("active");

    const category = btn.dataset.category;
    if (category === "All") {
      displayMenu(menu);
    } else {
      const filtered = menu.filter(item => item.category === category);
      displayMenu(filtered);
    }
  });
});

searchInput.addEventListener("keyup", () => {
  const value = searchInput.value.toLowerCase();
  const filtered = menu.filter(item => item.name.toLowerCase().includes(value));
  displayMenu(filtered);
});

document.getElementById("clear-cart").addEventListener("click", () => {
  cart = [];
  updateCart();
});

// ✅ ADD THIS HERE
document.getElementById("checkout").addEventListener("click", () => {
  if (cart.length === 0) {
    alert("Cart is empty! Add some items 😋");
    return;
  }
  window.location.href = "payment.html";
});

// Load menu first time
displayMenu(menu);


